let totalspacevalue = [];
let totalspacename = [];

context.panel.data.series.forEach((s) => {
  s.fields.forEach((f) => {
    if (f.type === 'number') {
      totalspacevalue.push(...f.values.toArray());
      totalspacename.push(f.name);
    }
  });
});

// Função para formatar bytes
function formatBytes(bytes, decimals = 2) {
  if (bytes === 0) return '0 Bytes';
  const k = 1024;
  const dm = decimals < 0 ? 0 : decimals;
  const sizes = ['Bytes', 'KB', 'MB', 'GB', 'TB', 'PB', 'EB', 'ZB', 'YB'];
  const i = Math.floor(Math.log(bytes) / Math.log(k));
  return parseFloat((bytes / Math.pow(k, i)).toFixed(dm)) + ' ' + sizes[i];
}

const sortedData = totalspacename.map((name, index) => ({
  name: name,
  value: totalspacevalue[index],
  formattedValue: formatBytes(totalspacevalue[index])
}));

// Determinar a cor com base no tema
const themeColor = context.grafana.theme.name === 'Light' ? '#000' : '#FFF';

return {
  title: {
    show: true,
    text: "Fylesystem Used Space",
    left: "center",
    top: "top",
    textStyle: {
      color: themeColor
    },
  },
  tooltip: {
    trigger: 'item',
    formatter: function (params) {
      const item = sortedData.find(item => item.name === params.name);
      return `<span style="color:${themeColor}">${params.name}: ${item.formattedValue}</span>`; // Cor do tooltip ajustada com base no tema
    }
  },
  legend: {
    top: '10%',
    left: 'center',
    textStyle: {
      color: themeColor
    }
  },
  toolbox: {
    show: true,
    feature: {
      mark: { show: true },
      dataView: { show: false, readOnly: false },
      restore: { show: true },
      saveAsImage: { show: true }
    }
  },
  series: [
    {
      name: 'Nightingale Chart',
      type: 'pie',
      radius: ['30%', '60%'],
      center: ['50%', '60%'],
      //roseType: 'area',
      itemStyle: {
        borderRadius: 8
      },
      label: {
        formatter: function (params) {
          const item = sortedData.find(item => item.name === params.name);
          return `${params.name}: ${item.formattedValue}`;
        },
        color: themeColor
      },
      data: sortedData.map(item => ({
        name: item.name,
        value: item.value,
        formattedValue: item.formattedValue,
      })),
    }
  ],
};

