options.helpers:
let vmstatus = [];
let vmdisksize = [];
let vmdiskusage = [];
let vmmemory = [];

const statusMap = {
  1: 'Other',
  2: 'Running',
  3: 'Off',
  4: 'Stopping',
  6: 'Saved',
  9: 'Paused',
  10: 'Starting',
  11: 'Reset'
};

context.panelData.series.map((s) => {
  vmstatus = s.fields.find((f) => f.name === 'State').values;
  vmdisksize = s.fields.find((f) => f.name === 'Disk size').values;
  vmdiskusage = s.fields.find((f) => f.name === 'Disk usage').values;
  vmmemory = s.fields.find((f) => f.name === 'Memory assigned').values;

  context.handlebars.registerHelper('vmdisksize', () => formatBytes(vmdisksize));
  context.handlebars.registerHelper('vmdiskusage', () => formatBytes(vmdiskusage));
  context.handlebars.registerHelper('vmmemory', () => formatBytes(vmmemory));
  context.handlebars.registerHelper('vmstatus', () => {
    const status = statusMap[vmstatus];
    return status ? status : 'Unknown status';
  });

  function formatBytes(bytes, decimals = 2) {
    if (bytes === 0) return '0';
    const k = 1024;
    const dm = decimals < 0 ? 0 : decimals;
    const sizes = ['B', 'KB', 'MB', 'GB', 'TB', 'PB', 'EB', 'ZB', 'YB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(dm)) + ' ' + sizes[i];
  }
});

